def rref(matrix):
    matrix = np.array(matrix, dtype=float)
    (r, c) = matrix.shape
    pr = 0
    pc = 0
    while pr < r and pc < c:
        pivot = np.argmax(np.abs(matrix[pr:, pc])) + pr
        matrix[[pr, pivot]] = matrix[[pivot, pr]]
        if np.abs(matrix[pr, pc]) < 1e-14:
            pc += 1
        else:
            matrix[pr] = matrix[pr] / matrix[pr, pc]
            for rr in range(r):
                if rr != pr:
                    matrix[rr] = matrix[rr] - matrix[rr, pc] * matrix[pr]
            pr += 1
            pc += 1
    return matrix.tolist()

