    y_pred = np.array(y_pred, dtype=bool)
    intersection = np.logical_and(y_true, y_pred)
    dice_score = 2.0 * intersection.sum() / (y_true.sum() + y_pred.sum())
    if y_true.sum() == 0 and y_pred.sum() == 0:
        dice_score = 1.0
    return round(dice_score, 3)

