def pattern_weaver(n, crystal_values, dimension):

    def softmax(values):
        e_x = np.exp(values - np.max(values))
        return e_x / e_x.sum(axis=0)
    crystal_values_matrix = np.array(crystal_values).reshape(n, dimension)
    attention_scores = np.dot(crystal_values_matrix, crystal_values_matrix.T)
    attention_scores = softmax(attention_scores)
    weighted_patterns = np.dot(attention_scores, crystal_values_matrix)
    return [round(pattern, 4) for pattern in weighted_patterns.flatten()]

