def calculate_bm25_scores(corpus, query, k1=1.5, b=0.75):
    avgdl = sum([len(doc) for doc in corpus]) / len(corpus)
    N = len(corpus)
    idf = {}
    for doc in corpus:
        for word in set(doc):
            if word not in idf:
                n_q = sum([1 for _doc in corpus if word in _doc])
                idf[word] = math.log((N - n_q + 0.5) / (n_q + 0.5))
    scores = []
    for doc in corpus:
        score = 0.0
        for word in query:
            if word in doc:
                freq = doc.count(word)
                numerator = freq * (k1 + 1)
                denominator = freq + k1 * (1 - b + b * len(doc) / avgdl)
                score += idf[word] * (numerator / denominator)
        scores.append(round(score, 3))
    return scores

import math
def elu(x: float, alpha: float=1.0) -> float:
    """
    Compute the ELU activation function.

    Args:
        x (float): Input value
        alpha (float): ELU parameter for negative values (default: 1.0)

    Returns:
        float: ELU activation value
    """
    if x >= 0:
        return round(x, 4)
    else:
        return round(alpha * (math.exp(x) - 1), 4)

import math
def softplus(x: float) -> float:
    """
    Compute the softplus activation function.

    Args:
        x: Input value

    Returns:
        The softplus value: log(1 + e^x)
    """
    if x > 500:
        return x
    elif x < -500:
        return math.exp(x)
    else:
        return round(math.log(1 + math.exp(x)), 4)

