import numpy as np
def orthonormal_basis(vectors: list[list[float]], tol: float=1e-10):
    vectors = np.array(vectors)
    basis = []
    for v in vectors:
        if len(basis) == 0:
            basis.append(v / np.linalg.norm(v))
        else:
            for b in basis:
                v -= np.dot(v, b) * b
            if np.linalg.norm(v) > tol:
                basis.append(v / np.linalg.norm(v))
    basis = [np.round(b.tolist(), 4) for b in basis]
    return basis

