def compute_policy_gradient(theta: np.ndarray, episodes: list[list[tuple[int, int, float]]]):
    (num_states, num_actions) = theta.shape
    policy_gradient = np.zeros_like(theta)
    for episode in episodes:
        T = len(episode)
        G = 0
        for t in reversed(range(T)):
            (s, a, r) = episode[t]
            probs = softmax(theta[s, :])
            G = G + r
            for a_ in range(num_actions):
                if a_ == a:
                    policy_gradient[s, a_] += (G - theta[s, a_]) * (1 - probs[a_])
                else:
                    policy_gradient[s, a_] -= theta[s, a_] * probs[a_]
    policy_gradient /= len(episodes)
    return policy_gradient.round(4).tolist()

import numpy as np
def find_treasure(start_x: float, learning_rate: float=0.1, tolerance: float=1e-06, max_iters: int=10000) -> float:

    def f(x):
        return x ** 4 - 3 * x ** 3 + 2

    def df(x):
        return 4 * x ** 3 - 9 * x ** 2
    x = start_x
    for _ in range(max_iters):
        grad = df(x)
        x = x - learning_rate * grad
        if np.abs(grad) < tolerance:
            break
    return round(x, 4)
