def softplus(x, deriv: bool=False):
    """
    Implements the SoftPlus activation function and its derivative.

    Parameters:
    x (scalar, list, or numpy array): Input values.
    deriv (bool): If True, returns the derivative of the SoftPlus function.

    Returns:
    list or float: The SoftPlus function or its derivative applied to x, rounded to the nearest 4th decimal.
    """
    x = np.array(x)
    if deriv:
        result = 1 / (1 + np.exp(-x))
    else:
        result = np.log1p(np.exp(x))
    result = np.round(result, 4)
    if np.isscalar(x):
        return result.item()
    else:
        return result.tolist()

