def lasso_regression(X, y, degree, reg_factor, n_iterations=1000, tol=1e-06):
    X = np.array(X)
    y = np.array(y)
    X_poly = np.ones((X.shape[0], degree + 1))
    for i in range(1, degree + 1):
        X_poly[:, i] = X ** i
    weights = np.zeros(degree + 1)
    for _ in range(n_iterations):
        weights_prev = weights.copy()
        weights[0] = np.mean(y - np.dot(X_poly[:, 1:], weights[1:]))
        for j in range(1, degree + 1):
            rho = np.dot(X_poly[:, j], y - np.dot(X_poly, weights) + weights[j] * X_poly[:, j])
            weights[j] = soft_threshold(rho, reg_factor) / np.dot(X_poly[:, j], X_poly[:, j])
        if np.max(np.abs(weights - weights_prev)) < tol:
            break
    weights = [round(w, 4) for w in weights]
    return weights
def soft_threshold(rho, lambda_):
    if rho < -lambda_:
        return rho + lambda_
    elif rho > lambda_:
        return rho - lambda_
    else:
        return 0

