class FPTree(object):

    def __init__(self):
        self.root = FPNode(None)
        self.header_table = defaultdict(None)
        self.item_counts = Counter()

    def add(self, transaction):
        current_node = self.root
        for item in transaction:
            self.item_counts[item] += 1
            current_node = current_node.add(item)

    def build_header_table(self):
        for (item, count) in self.item_counts.items():
            if count >= min_support:
                self.header_table[item] = self.root.search(item)

    def mine(self):
        if self.root.children:
            self.header_table = sorted(self.header_table.items(), key=lambda x: x[1].count, reverse=True)
            for (item, node) in self.header_table:
                suffix = []
                current_node = node.parent
                while current_node.parent is not None:
                    suffix.append(current_node.item)
                    current_node = current_node.parent
                if suffix:
                    yield (suffix, node.count)
                conditional_tree = FPTree()
                for item in reversed(suffix):
                    conditional_tree.add(self.root.search(item).prefix())
                for result in conditional_tree.mine():
