class FPNode(object):

    def __init__(self, item):
        self.item = item
        self.count = 1
        self.parent = None
        self.children = {}
        self.next = None

    def add(self, child):
        if child.item in self.children:
            node = self.children[child.item]
            node.count += 1
        else:
            child.parent = self
            self.children[child.item] = child
            if self.next is not None:
                current_node = self.next
                while current_node.item != child.item:
                    if current_node.item is None:
                        current_node.item = child.item
                        child.next = current_node.next
                        current_node.next = child
                        return child
                    current_node = current_node.next
                current_node.count += 1
            else:
                self.next = child
        return child

    def search(self, item):
        if self.item == item:
            return self
        else:
            current_node = self.next
            while current_node is not None:
                if current_node.item == item:
                    return current_node
                current_node = current_node.next
            return None

    def prefix(self):
        prefix = []
        current_node = self
        while current_node.item is not None:
            prefix.append(current_node.item)
            current_node = current_node.parent
        return list(reversed(prefix))
def fp_growth(transactions, min_support):
    tree = FPTree()
    for transaction in transactions:
        transaction.sort()
        tree.add(transaction)
    tree.build_header_table()
    return list(tree.mine())

