def escape_fire_maze(grid: list[str]) -> int:
    n = len(grid)
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    agent_queue = deque([(0, 0)])
    fire_queue = deque([(i, j) for i in range(n) for j in range(n) if grid[i][j] == 'F'])
    agent_visited = set([(0, 0)])
    fire_visited = set(fire_queue)
    minutes = 0
    while agent_queue:
        for _ in range(len(agent_queue)):
            (i, j) = agent_queue.popleft()
            if (i, j) == (n - 1, n - 1):
                return minutes
            for (di, dj) in directions:
                (ni, nj) = (i + di, j + dj)
                if 0 <= ni < n and 0 <= nj < n and (grid[ni][nj] != '#') and ((ni, nj) not in fire_visited):
                    agent_queue.append((ni, nj))
                    agent_visited.add((ni, nj))
        for _ in range(len(fire_queue)):
            (i, j) = fire_queue.popleft()
            for (di, dj) in directions:
                (ni, nj) = (i + di, j + dj)
                if 0 <= ni < n and 0 <= nj < n and (grid[ni][nj] != '#') and ((ni, nj) not in fire_visited):
                    fire_queue.append((ni, nj))
                    fire_visited.add((ni, nj))
        minutes += 1
    return -1

