def gini_impurity(y):
    (_, counts) = np.unique(y, return_counts=True)
    probabilities = counts / counts.sum()
    return 1 - np.sum(np.square(probabilities))
def split_data(X, y, feature_index, threshold):
    left_mask = X[:, feature_index] < threshold
    right_mask = X[:, feature_index] >= threshold
    return ((X[left_mask], y[left_mask]), (X[right_mask], y[right_mask]))
def find_best_split(X, y):
    best_gain = -np.inf
    (best_feature_index, best_threshold) = (None, None)
    parent_gini = gini_impurity(y)
    for feature_index in range(X.shape[1]):
        sorted_indices = np.argsort(X[:, feature_index])
        (sorted_X, sorted_y) = (X[sorted_indices], y[sorted_indices])
        thresholds = (sorted_X[1:] + sorted_X[:-1]) / 2
        for threshold in thresholds:
            ((_, left_y), (_, right_y)) = split_data(sorted_X, sorted_y, feature_index, threshold)
            if len(left_y) == 0 or len(right_y) == 0:
                continue
            left_gini = gini_impurity(left_y)
            right_gini = gini_impurity(right_y)
            child_gini = len(left_y) / len(y) * left_gini + len(right_y) / len(y) * right_gini
            gain = parent_gini - child_gini
            if gain > best_gain:
                best_gain = gain
                best_feature_index = feature_index
                best_threshold = threshold
    return (best_feature_index, best_threshold)
def build_tree(X, y, depth=0, max_depth=None):
    if len(np.unique(y)) == 1 or (max_depth is not None and depth == max_depth):
        return Node(np.argmax(np.bincount(y)))
    (feature_index, threshold) = find_best_split(X, y)
    if feature_index is None:
        return Node(np.argmax(np.bincount(y)))
    ((X_left, y_left), (X_right, y_right)) = split_data(X, y, feature_index, threshold)
    left = build_tree(X_left, y_left, depth + 1, max_depth)
    right = build_tree(X_right, y_right, depth + 1, max_depth)
    return Node(np.argmax(np.bincount(y))).fit(feature_index, threshold, left, right)
