import numpy as np
def pegasos_kernel_svm(data: np.ndarray, labels: np.ndarray, kernel='linear', lambda_val=0.01, iterations=100, sigma=1.0):

    def linear_kernel(x1, x2):
        return np.dot(x1, x2)

    def rbf_kernel(x1, x2, sigma):
        return np.exp(-np.linalg.norm(x1 - x2) ** 2 / (2 * sigma ** 2))

    def compute_kernel_matrix(data, kernel, sigma):
        n_samples = data.shape[0]
        K = np.zeros((n_samples, n_samples))
        for i in range(n_samples):
            for j in range(n_samples):
                if kernel == 'linear':
                    K[i, j] = linear_kernel(data[i], data[j])
                elif kernel == 'rbf':
                    K[i, j] = rbf_kernel(data[i], data[j], sigma)
        return K
    (n_samples, n_features) = data.shape
    alpha = np.zeros(n_samples)
    bias = 0.0
    K = compute_kernel_matrix(data, kernel, sigma)
    for t in range(1, iterations + 1):
        learning_rate = 1.0 / (lambda_val * t)
        for i in range(n_samples):
            prediction = np.sum(alpha * labels * K[:, i]) + bias
            if labels[i] * prediction < 1:
                alpha[i] = alpha[i] + learning_rate * labels[i]
                bias = bias + learning_rate * labels[i]
        alpha = alpha * min(1, 1.0 / (lambda_val * np.sqrt(t)) * (1.0 / np.sqrt(np.sum(alpha * labels * K.dot(alpha * labels)))))
    return (alpha.round(4).tolist(), round(bias, 4))
