def isolation_tree_path_length(data: np.ndarray) -> list[int]:
    """Compute the depth of the leaf reached by every sample in a random isolation tree.

    The tree is built following the rules described in the task statement and the
    random seed must be set to 0 so that results are reproducible.

    Args:
        data: A 2-D NumPy array of shape (n_samples, n_features).

    Returns:
        A list of length *n_samples* where the *i-th* element is the number of
        edges from the root to the leaf that contains the *i-th* sample.
    """
    np.random.seed(0)

    def build_tree(indices):
        if len(indices) < 3:
            return {'indices': indices}
        feature_index = np.random.choice(data.shape[1])
        feature_values = data[indices, feature_index]
        (down, up) = (np.min(feature_values), np.max(feature_values))
        v = np.random.uniform(down, up)
        left_indices = indices[feature_values <= v]
        right_indices = indices[feature_values > v]
        return {'feature_index': feature_index, 'value': v, 'left': build_tree(left_indices), 'right': build_tree(right_indices)}

    def traverse_tree(node, sample_index, depth):
        if 'indices' in node:
            return depth
        feature_index = node['feature_index']
        value = node['value']
        if data[sample_index, feature_index] <= value:
            return traverse_tree(node['left'], sample_index, depth + 1)
        else:
            return traverse_tree(node['right'], sample_index, depth + 1)
    n_samples = data.shape[0]
    tree = build_tree(np.arange(n_samples))
    depths = [traverse_tree(tree, i, 0) for i in range(n_samples)]
    return depths

