def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    """Trains an AdaBoost ensemble using decision stumps and predicts the class labels for X_test."""
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    X_test = np.array(X_test)
    n_samples = X_train.shape[0]
    weights = np.ones(n_samples) / n_samples
    stumps = []
    alphas = []

    def train_stump():
        min_error = float('inf')
        best_stump = {}
        best_polarity = 1
        for feature_index in range(X_train.shape[1]):
            thresholds = np.unique(X_train[:, feature_index])
            for threshold in thresholds:
                for polarity in [1, -1]:
                    predictions = np.ones(n_samples)
                    predictions[X_train[:, feature_index] * polarity < threshold * polarity] = -1
                    weighted_error = (weights * (predictions != y_train)).sum()
                    if weighted_error < min_error:
                        min_error = weighted_error
                        best_stump = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity}
                        best_polarity = polarity
        return (best_stump, min_error, best_polarity)
    for _ in range(n_estimators):
        (stump, error, polarity) = train_stump()
        if error == 0:
            break
        alpha = 0.5 * np.log((1 - error) / (error + 1e-10))
        predictions = np.ones(n_samples)
        predictions[X_train[:, stump['feature_index']] * stump['polarity'] < stump['threshold'] * stump['polarity']] = -1
        weights *= np.exp(-alpha * y_train * predictions)
        weights /= weights.sum()
        stumps.append(stump)
        alphas.append(alpha)

    def predict(X):
        n_samples = X.shape[0]
        stump_predictions = np.zeros(n_samples)
        for (stump, alpha) in zip(stumps, alphas):
            predictions = np.ones(n_samples)
            predictions[X[:, stump['feature_index']] * stump['polarity'] < stump['threshold'] * stump['polarity']] = -1
            stump_predictions += alpha * predictions
        return np.where(stump_predictions > 0, 1, 0).tolist()
    return predict(X_test)

