def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    def gini_impurity(y):
        m = len(y)
        if m == 0:
            return 0.0
        counts = np.bincount(y)
        probabilities = counts / m
        return 1.0 - np.sum(probabilities ** 2)

    def best_split(X, y):
        best_gini = float('inf')
        best_feature = None
        best_threshold = None
        n_features = X.shape[1]
        for feature in range(n_features):
            sorted_indices = np.argsort(X[:, feature])
            X_sorted = X[sorted_indices, feature]
            y_sorted = y[sorted_indices]
            for i in range(1, len(y_sorted)):
                if X_sorted[i] != X_sorted[i - 1]:
                    threshold = (X_sorted[i] + X_sorted[i - 1]) / 2
                    left_y = y_sorted[:i]
                    right_y = y_sorted[i:]
                    gini_left = gini_impurity(left_y)
                    gini_right = gini_impurity(right_y)
                    gini = len(left_y) / len(y_sorted) * gini_left + len(right_y) / len(y_sorted) * gini_right
                    if gini < best_gini:
                        best_gini = gini
                        best_feature = feature
                        best_threshold = threshold
        return (best_feature, best_threshold)

    def majority_class(y):
        return np.bincount(y).argmax()

    def build_tree(X, y, depth=0):
        if max_depth is not None and depth >= max_depth:
            return majority_class(y)
        if len(np.unique(y)) == 1:
            return y[0]
        (feature, threshold) = best_split(X, y)
        if feature is None or threshold is None:
            return majority_class(y)
        left_indices = X[:, feature] < threshold
        right_indices = X[:, feature] >= threshold
        if not np.any(left_indices) or not np.any(right_indices):
            return majority_class(y)
        left_tree = build_tree(X[left_indices], y[left_indices], depth + 1)
        right_tree = build_tree(X[right_indices], y[right_indices], depth + 1)
        return (feature, threshold, left_tree, right_tree)

    def predict_tree(tree, x):
        if isinstance(tree, int):
            return tree
        (feature, threshold, left_tree, right_tree) = tree
        if x[feature] < threshold:
            return predict_tree(left_tree, x)
        else:
            return predict_tree(right_tree, x)
    tree = build_tree(X_train, y_train)
    predictions = [predict_tree(tree, x) for x in X_test]
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_vectors(x, y):
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except (ValueError, TypeError):
            return False
        if x.ndim != 1 or y.ndim != 1:
            return False
        if x.size != y.size or x.size == 0:
            return False
        return (x, y)

    def euclidean(x, y):
        return np.sqrt(np.sum((x - y) ** 2))

    def manhattan(x, y):
        return np.sum(np.abs(x - y))

    def chebyshev(x, y):
        return np.max(np.abs(x - y))

    def cosine(x, y):
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        return 1 - np.dot(x, y) / (norm_x * norm_y)
    metric_functions = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    if name not in metric_functions:
        return lambda x, y: -1

    def d(x, y):
        validated = validate_vectors(x, y)
        if validated is False:
            return -1
        (x, y) = validated
        return round(metric_functions[name](x, y), 4)
    return d

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def hand_value(hand: list[int]) -> int:
        """Calculate the value of a hand, considering the usable ace."""
        value = sum(hand)
        aces = hand.count(1)
        while value > 21 and aces > 0:
            value -= 10
            aces -= 1
        return value

    def is_natural_blackjack(hand: list[int]) -> bool:
        """Check if a hand is a natural blackjack."""
        return len(hand) == 2 and 1 in hand and (10 in hand)
    player_value = hand_value(player)
    dealer_value = hand_value(dealer)
    if player_value > 21:
        return -1.0
    if dealer_value > 21:
        return 1.0
    if is_natural_blackjack(player):
        return 1.5
    if player_value > dealer_value:
        return 1.0
    elif player_value < dealer_value:
        return -1.0
    else:
        return 0.0

