import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
    sorted_items = sorted(frequent_items, key=lambda x: (-frequent_items[x], x))
    header_table = defaultdict(list)
    fp_tree = FPTree()
    for transaction in transactions:
        filtered_transaction = [item for item in transaction if item in frequent_items]
        sorted_transaction = sorted(filtered_transaction, key=lambda x: sorted_items.index(x))
        fp_tree.insert(sorted_transaction, header_table)
    frequent_itemsets = []
    fp_growth_mine(fp_tree.root, header_table, [], frequent_itemsets, min_sup)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
class FPTree:

    def __init__(self):
        self.root = FPNode(None, None, None)
        self.size = 0

    def insert(self, transaction, header_table):
        current_node = self.root
        for item in transaction:
            child = current_node.get_child(item)
            if child is not None:
                child.count += 1
            else:
                child = FPNode(item, 1, current_node)
                current_node.children[item] = child
                header_table[item].append(child)
            current_node = child
        self.size += 1
class FPNode:

    def __init__(self, item, count, parent):
        self.item = item
        self.count = count
        self.parent = parent
        self.children = defaultdict(FPNode)

    def get_child(self, item):
        return self.children.get(item, None)
def fp_growth_mine(node, header_table, prefix, frequent_itemsets, min_sup):
    if node.item is not None:
        prefix = [node.item] + prefix
    if not node.children:
        for i in range(1, len(prefix) + 1):
            for combination in itertools.combinations(prefix, i):
                frequent_itemsets.append(tuple(sorted(combination)))
        return
    for item in reversed(header_table):
        conditional_pattern_base = []
        for node in header_table[item]:
            path = []
            parent = node.parent
            while parent.item is not None:
                path.append(parent.item)
                parent = parent.parent
            if path:
                conditional_pattern_base.append((path, node.count))
        conditional_tree = FPTree()
        conditional_header_table = defaultdict(list)
        for (path, count) in conditional_pattern_base:
            conditional_tree.insert(reversed(path), conditional_header_table)
        if conditional_tree.root.children:
            fp_growth_mine(conditional_tree.root, conditional_header_table, prefix, frequent_itemsets, min_sup)
