import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)

    def c(n):
        if n == 1:
            return 0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def expected_path_length(n):
        if n <= 1:
            return 0
        return c(n) + 2 * (np.log(n - 1) - (n - 1) / n)

    def build_tree(subsample):
        if len(subsample) <= 1 or len(subsample) == sample_size:
            return (None, subsample)
        feature_idx = np.random.choice(subsample.shape[1])
        (feature_min, feature_max) = (np.min(subsample[:, feature_idx]), np.max(subsample[:, feature_idx]))
        if feature_min == feature_max:
            return (None, subsample)
        threshold = np.random.uniform(feature_min, feature_max)
        left_indices = subsample[:, feature_idx] < threshold
        right_indices = subsample[:, feature_idx] >= threshold
        left_subsample = subsample[left_indices]
        right_subsample = subsample[right_indices]
        (left_tree, left_subsample) = build_tree(left_subsample)
        (right_tree, right_subsample) = build_tree(right_subsample)
        return ((feature_idx, threshold, left_tree, right_tree), None)

    def path_length(x, tree, current_height=0):
        if tree is None:
            return current_height + c(len(x))
        (feature_idx, threshold, left_tree, right_tree) = tree
        if x[feature_idx] < threshold:
            return path_length(x, left_tree, current_height + 1)
        else:
            return path_length(x, right_tree, current_height + 1)
    trees = []
    for _ in range(n_trees):
        if sample_size >= data.shape[0]:
            subsample = data
        else:
            subsample_indices = np.random.choice(data.shape[0], sample_size, replace=False)
            subsample = data[subsample_indices]
        (tree, _) = build_tree(subsample)
        trees.append(tree)
    path_lengths = np.zeros(data.shape[0])
    for (i, x) in enumerate(data):
        path_lengths[i] = np.mean([path_length(x, tree) for tree in trees])
    n = data.shape[0]
    phi = 2 * math.log(n - 1) - 2 * (n - 1) / n
    anomaly_scores = 2 ** (-path_lengths / phi)
    threshold = np.percentile(anomaly_scores, (1 - epsilon) * 100)
    outliers = np.where(anomaly_scores >= threshold)[0]
    return sorted(outliers)

