import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """Predicts the labels for X_test using a Maximum Entropy classifier trained on X_train and y_train."""
    (n_samples, n_features) = X_train.shape
    labels = set(y_train)
    n_labels = len(labels)
    empirical_exp = defaultdict(float)
    for (x, y) in zip(X_train, y_train):
        for (feature_index, feature_value) in enumerate(x):
            empirical_exp[feature_index, feature_value, y] += 1
    for key in empirical_exp:
        empirical_exp[key] /= n_samples
    model_exp = defaultdict(float)
    weights = defaultdict(float)
    for iteration in range(n_iter):
        model_exp.clear()
        Z = np.zeros(n_samples)
        for (i, x) in enumerate(X_train):
            Z_x = 0
            for y in labels:
                score = 0
                for (feature_index, feature_value) in enumerate(x):
                    score += weights[feature_index, feature_value, y]
                Z_x += math.exp(score)
            Z[i] = Z_x
        for (i, (x, y)) in enumerate(zip(X_train, y_train)):
            for (feature_index, feature_value) in enumerate(x):
                score = 0
                for y_prime in labels:
                    score_prime = 0
                    for (feature_index_prime, feature_value_prime) in enumerate(x):
                        score_prime += weights[feature_index_prime, feature_value_prime, y_prime]
                    score_prime = math.exp(score_prime) / Z[i]
                    model_exp[feature_index, feature_value, y_prime] += score_prime
                model_exp[feature_index, feature_value, y] += math.exp(score) / Z[i]
        for key in model_exp:
            model_exp[key] /= n_samples
        max_update = 0
        for key in empirical_exp:
            update = empirical_exp[key] - model_exp[key]
            weights[key] += update
            max_update = max(max_update, abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for x in X_test:
        max_score = -float('inf')
        predicted_label = None
        for y in labels:
            score = 0
            for (feature_index, feature_value) in enumerate(x):
                score += weights[feature_index, feature_value, y]
            if score > max_score:
                max_score = score
                predicted_label = y
        predictions.append(predicted_label)
    return predictions

