def local_outlier_factor(data, k, epsilon=1.5):
    """Compute the Local Outlier Factor (LOF) for each sample in the dataset."""
    n = len(data)
    distance_matrix = np.zeros((n, n))
    for i in range(n):
        for j in range(i + 1, n):
            distance = np.sqrt(np.sum((data[i] - data[j]) ** 2))
            distance_matrix[i, j] = distance
            distance_matrix[j, i] = distance
    k_distances = np.zeros(n)
    k_neighbors = [None] * n
    for i in range(n):
        distances = distance_matrix[i]
        sorted_indices = np.argsort(distances)
        k_distances[i] = distances[sorted_indices[k]]
        k_neighbors[i] = sorted_indices[1:k + 1]

    def reachability_distance(p, q):
        return max(k_distances[q], distance_matrix[p, q])
    lrd = np.zeros(n)
    for i in range(n):
        sum_reach_dist = 0
        for neighbor in k_neighbors[i]:
            sum_reach_dist += reachability_distance(i, neighbor)
        lrd[i] = k / sum_reach_dist
    lof_scores = np.zeros(n)
    for i in range(n):
        sum_lrd_neighbors = 0
        for neighbor in k_neighbors[i]:
            sum_lrd_neighbors += lrd[neighbor]
        lof_scores[i] = sum_lrd_neighbors / k / lrd[i]
    lof_scores = np.round(lof_scores, 4)
    outlier_indices = np.where(lof_scores > epsilon)[0]
    outlier_indices = sorted(outlier_indices)
    return (list(lof_scores), outlier_indices)
