import numpy as np
from collections import Counter
data = np.arange(5).reshape(-1, 1)
def isolation_tree_path_length(data: np.ndarray) -> list[int]:
    np.random.seed(0)
    n_samples = data.shape[0]

    def build_tree(indices):
        if len(indices) < 3:
            return {'type': 'leaf', 'indices': indices}
        n_features = data.shape[1]
        f = np.random.randint(0, n_features)
        current_data = data[indices, f]
        down = current_data.min()
        up = current_data.max()
        v = np.random.uniform(down, up) if down < up else down
        left_indices = []
        right_indices = []
        for idx in indices:
            if data[idx, f] <= v:
                left_indices.append(idx)
            else:
                right_indices.append(idx)
        left_child = build_tree(left_indices)
        right_child = build_tree(right_indices)
        return {'type': 'internal', 'feature': f, 'value': v, 'left': left_child, 'right': right_child}
    root = build_tree(list(range(n_samples)))

    def get_depth(tree_node, sample_i, current_depth):
        if tree_node['type'] == 'leaf':
            return current_depth
        else:
            f = tree_node['feature']
            v = tree_node['value']
            sample_val = data[sample_i, f]
            if sample_val <= v:
                next_node = tree_node['left']
            else:
                next_node = tree_node['right']
            return get_depth(next_node, sample_i, current_depth + 1)
    depths = []
    for i in range(n_samples):
        depths.append(get_depth(root, i, 0))
    return depths

