minsup = 0.6
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    N = len(transactions)
    if N == 0:
        return {}
    threshold = math.ceil(minsup * N)
    trans_sets = [set(transaction) for transaction in transactions]
    item_counts = Counter()
    for t_set in trans_sets:
        for item in t_set:
            item_counts[item,] += 1
    freq_1 = {item: count for (item, count) in item_counts.items() if count >= threshold}
    if not freq_1:
        return {}
    all_frequent_items = {item[0] for item in freq_1}
    result = dict(freq_1)
    current_level = list(freq_1.keys())
    k = 2
    while current_level:
        candidates = []
        prev_itemsets_set = set(current_level)
        for combo in combinations(all_frequent_items, k):
            all_subsets_frequent = True
            for subset in combinations(combo, k - 1):
                sorted_subset = tuple(sorted(subset))
                if sorted_subset not in prev_itemsets_set:
                    all_subsets_frequent = False
                    break
            if all_subsets_frequent:
                candidates.append(tuple(sorted(combo)))
        candidate_support = {}
        for candidate in candidates:
            count = 0
            for t_set in trans_sets:
                if set(candidate).issubset(t_set):
                    count += 1
            if count >= threshold:
                candidate_support[candidate] = count
        for (itemset, support) in candidate_support.items():
            result[itemset] = support
        current_level = list(candidate_support.keys())
        k += 1
    return result

import numpy as np
def is_stochastic(X: list[list[float]] | 'np.ndarray') -> bool:
    """Return True if *X* is a row-stochastic matrix, otherwise False.

    A matrix is row-stochastic when every element is a probability (0 ≤ p ≤ 1)
    and each row sums to 1 (within a small numerical tolerance).

    Args:
        X: Matrix given as a list of lists or a NumPy array.

    Returns:
        bool: True if *X* is row-stochastic, False otherwise.
    """
    if isinstance(X, np.ndarray):
        if X.ndim != 2:
            return False
        if not np.issubdtype(X.dtype, np.number):
            return False
        if not np.all((X >= 0) & (X <= 1)):
            return False
        row_sums = X.sum(axis=1)
        return np.allclose(row_sums, 1, atol=1e-08, rtol=0)
    else:
        if not isinstance(X, list) or not X:
            return False
        row_length = None
        for row in X:
            if not isinstance(row, list):
                return False
            if row_length is None:
                row_length = len(row)
                if row_length == 0:
                    return False
            elif len(row) != row_length or len(row) == 0:
                return False
        for row in X:
            for elem in row:
                if not isinstance(elem, (int, float)):
                    return False
        for row in X:
            for elem in row:
                if not 0 <= elem <= 1:
                    return False
        for row in X:
            s = sum(row)
            if not np.allclose(s, 1, atol=1e-08, rtol=0):
                return False
        return True

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    if not observations:
        return -1
    n = len(S)
    if n == 0:
        return -1
    if len(A) != n:
        return -1
    for row in A:
        if len(row) != n:
            return -1
    if len(B) != n:
        return -1
    if not B[0]:
        return -1
    m = len(B[0])
    for row in B:
        if len(row) != m:
            return -1
    for obs in observations:
        if obs < 0 or obs >= m:
            return -1
    T = len(observations)
    alpha_prev = [S[i] * B[i][observations[0]] for i in range(n)]
    for t in range(1, T):
        current_obs = observations[t]
        alpha_current = []
        for i in range(n):
            sum_trans = sum((alpha_prev[j] * A[j][i] for j in range(n)))
            current_val = sum_trans * B[i][current_obs]
            alpha_current.append(current_val)
        alpha_prev = alpha_current
    total = sum(alpha_prev)
    return round(total, 4)

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions:
        return []
    items_count = Counter()
    for transaction in transactions:
        items_count.update(transaction)
    frequent_items = {item: count for (item, count) in items_count.items() if count >= min_support}
    if not frequent_items:
        return []
    sorted_frequent = sorted(frequent_items.keys(), key=lambda item: (-frequent_items[item], item))
    item_support = frequent_items

    class FPNode:

        def __init__(self, name, count, parent):
            self.name = name
            self.count = count
            self.parent = parent
            self.children = {}
            self.next = None
    root = FPNode(None, 0, None)
    header_table = {item: None for item in sorted_frequent}
    for transaction in transactions:
        filtered = [item for item in transaction if item in frequent_items]
        filtered.sort(key=lambda item: (-item_support[item], item))
        if not filtered:
            continue
        current = root
        for item in filtered:
            if item in current.children:
                current.children[item].count += 1
            else:
                new_node = FPNode(item, 1, current)
                current.children[item] = new_node
                if header_table[item] is None:
                    header_table[item] = new_node
                else:
                    last = header_table[item]
                    while last.next is not None:
                        last = last.next
                    last.next = new_node
            current = current.children[item]

    def mine_tree(header_table, tree_root, min_support, prefix, frequent_itemsets, item_support):
        sorted_items = [item for item in header_table if item_support[item] >= min_support]
        sorted_items.sort(key=lambda item: (item_support[item], item))
        for item in reversed(sorted_items):
            new_freqset = prefix + [item]
            new_freqset_sorted = sorted(new_freqset)
            frequent_itemsets.append(new_freqset_sorted)
            cond_patterns = []
            current_node = header_table[item]
            while current_node is not None:
                path = []
                node = current_node.parent
                while node is not None and node.name is not None:
                    path.append((node.name, node.count))
                    node = node.parent
                path.reverse()
                cond_patterns.append((path, current_node.count))
                current_node = current_node.next
            cond_support = Counter()
            for (path, occ_count) in cond_patterns:
                for (item_name, _) in path:
                    cond_support[item_name] += occ_count
            cond_frequent = {item: count for (item, count) in cond_support.items() if count >= min_support}
            if not cond_frequent:
                continue
            sorted_cond_frequent = sorted(cond_frequent.keys(), key=lambda item: (-cond_frequent[item], item))
            cond_header_table = {item: None for item in sorted_cond_frequent}
            cond_root = FPNode(None, 0, None)
            for (path, occ_count) in cond_patterns:
                filtered_path = [(item_name, item_count) for (item_name, item_count) in path if item_name in cond_frequent]
                sorted_items_in_path = sorted([item_name for (item_name, _) in filtered_path], key=lambda x: (-cond_frequent[x], x))
                current_cond = cond_root
                for item_name in sorted_items_in_path:
                    if item_name in current_cond.children:
                        current_cond.children[item_name].count += occ_count
                    else:
                        new_node = FPNode(item_name, occ_count, current_cond)
                        current_cond.children[item_name] = new_node
                        if cond_header_table[item_name] is None:
                            cond_header_table[item_name] = new_node
                        else:
                            last = cond_header_table[item_name]
                            while last.next is not None:
                                last = last.next
                            last.next = new_node
                    current_cond = current_cond.children[item_name]
            mine_tree(cond_header_table, cond_root, min_support, new_freqset_sorted, frequent_itemsets, cond_frequent)
    frequent_itemsets = []
    mine_tree(header_table, root, min_support, [], frequent_itemsets, item_support)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets

