import numpy as np
def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    """Implements AdaBoost with decision stumps as weak learners."""
    y_train_bin = np.array([-1 if label == 0 else 1 for label in y_train])
    X_train_np = np.array(X_train)
    n_samples = len(X_train)
    weights = np.full(n_samples, 1 / n_samples)
    stumps = []
    alphas = []
    for _ in range(n_estimators):
        best_stump = None
        best_error = float('inf')
        for feature_idx in range(X_train_np.shape[1]):
            feature_values = X_train_np[:, feature_idx]
            sorted_unique = np.unique(feature_values)
            if len(sorted_unique) == 1:
                thresholds = [sorted_unique[0]]
            else:
                thresholds = [(sorted_unique[i] + sorted_unique[i + 1]) / 2 for i in range(len(sorted_unique) - 1)]
            for threshold in thresholds:
                for polarity in [1, -1]:
                    if polarity == 1:
                        predictions = np.where(feature_values < threshold, 1, -1)
                    else:
                        predictions = np.where(feature_values >= threshold, 1, -1)
                    error = np.sum(weights * (predictions != y_train_bin))
                    if error < best_error:
                        best_error = error
                        best_stump = {'feature_idx': feature_idx, 'threshold': threshold, 'polarity': polarity}
                    elif error == best_error:
                        if feature_idx == best_stump['feature_idx']:
                            current_threshold = threshold
                            best_threshold = best_stump['threshold']
                            if current_threshold < best_threshold:
                                best_stump = {'feature_idx': feature_idx, 'threshold': threshold, 'polarity': polarity}
                            elif current_threshold == best_threshold:
                                if polarity == 1 and best_stump['polarity'] == -1:
                                    best_stump = {'feature_idx': feature_idx, 'threshold': threshold, 'polarity': polarity}
        if best_error == 0:
            alpha = 0.5 * np.log((1 - best_error) / (best_error + 1e-10))
            stumps.append(best_stump)
            alphas.append(alpha)
            break
        alpha = 0.5 * np.log((1 - best_error) / (best_error + 1e-10))
        stumps.append(best_stump)
        alphas.append(alpha)
        feature_idx = best_stump['feature_idx']
        threshold = best_stump['threshold']
        polarity = best_stump['polarity']
        feature_values = X_train_np[:, feature_idx]
        if polarity == 1:
            predictions = np.where(feature_values < threshold, 1, -1)
        else:
            predictions = np.where(feature_values >= threshold, 1, -1)
        product = y_train_bin * predictions
        weights = weights * np.exp(-alpha * product)
        weights = weights / np.sum(weights)
    X_test_np = np.array(X_test)
    predictions = []
    for x in X_test_np:
        total = 0.0
        for (stump, alpha_t) in zip(stumps, alphas):
            feature_idx = stump['feature_idx']
            threshold = stump['threshold']
            polarity = stump['polarity']
            value = x[feature_idx]
            if polarity == 1:
                h = 1 if value < threshold else -1
            else:
                h = 1 if value >= threshold else -1
            total += alpha_t * h
        predicted_class = 1 if total > 0 else 0
        predictions.append(predicted_class)
    return predictions

import numpy as np
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    def compute_gini(y):
        """Compute Gini impurity for a set of class labels."""
        if len(y) == 0:
            return 0.0
        (classes, counts) = np.unique(y, return_counts=True)
        probabilities = counts / len(y)
        return 1 - np.sum(probabilities ** 2)

    def majority_class(y):
        """Return the majority class label (most frequent)."""
        (classes, counts) = np.unique(y, return_counts=True)
        max_index = np.argmax(counts)
        return classes[max_index]

    def find_best_split(X, y):
        """Find the best feature and threshold to split on, maximizing Gini gain."""
        best_gain = -1
        best_feature = None
        best_threshold = None
        parent_gini = compute_gini(y)
        n_samples = len(y)
        n_features = X.shape[1]
        for feature_idx in range(n_features):
            x = X[:, feature_idx]
            sorted_indices = np.argsort(x)
            sorted_x = x[sorted_indices]
            sorted_y = y[sorted_indices]
            prev_val = None
            for i in range(len(sorted_x)):
                current_val = sorted_x[i]
                if prev_val is not None and current_val != prev_val:
                    threshold = (prev_val + current_val) / 2
                    left_y = sorted_y[:i]
                    right_y = sorted_y[i:]
                    gini_left = compute_gini(left_y)
                    gini_right = compute_gini(right_y)
                    weighted_gini = len(left_y) / n_samples * gini_left + len(right_y) / n_samples * gini_right
                    gain = parent_gini - weighted_gini
                    if gain > best_gain:
                        best_gain = gain
                        best_feature = feature_idx
                        best_threshold = threshold
                prev_val = current_val
        if best_gain > 0:
            return (best_feature, best_threshold)
        else:
            return (None, None)

    def build_tree(X, y, current_depth):
        """Recursively build the decision tree."""
        if len(np.unique(y)) == 1:
            return {'is_leaf': True, 'label': y[0]}
        if max_depth is not None and current_depth >= max_depth:
            return {'is_leaf': True, 'label': majority_class(y)}
        (best_feature, best_threshold) = find_best_split(X, y)
        if best_feature is None:
            return {'is_leaf': True, 'label': majority_class(y)}
        left_mask = X[:, best_feature] <= best_threshold
        right_mask = X[:, best_feature] > best_threshold
        (X_left, y_left) = (X[left_mask], y[left_mask])
        (X_right, y_right) = (X[right_mask], y[right_mask])
        left_subtree = build_tree(X_left, y_left, current_depth + 1)
        right_subtree = build_tree(X_right, y_right, current_depth + 1)
        return {'is_leaf': False, 'feature': best_feature, 'threshold': best_threshold, 'left': left_subtree, 'right': right_subtree}

    def predict_sample(tree, sample):
        """Predict the class label for a single sample."""
        if tree['is_leaf']:
            return tree['label']
        else:
            feature_idx = tree['feature']
            threshold = tree['threshold']
            if sample[feature_idx] <= threshold:
                return predict_sample(tree['left'], sample)
            else:
                return predict_sample(tree['right'], sample)
    tree = build_tree(X_train, y_train, current_depth=0)
    predictions = []
    for sample in X_test:
        predictions.append(predict_sample(tree, sample))
    return predictions
