import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported = {'euclidean', 'manhattan', 'chebyshev', 'cosine'}
    if name not in supported:
        return lambda x, y: -1

    def d(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return -1
        try:
            x_arr = np.array(x)
            y_arr = np.array(y)
        except:
            return -1
        if x_arr.ndim != 1 or y_arr.ndim != 1:
            return -1
        if x_arr.size == 0 or y_arr.size == 0:
            return -1
        if x_arr.shape[0] != y_arr.shape[0]:
            return -1
        if not (np.issubdtype(x_arr.dtype, np.number) and np.issubdtype(y_arr.dtype, np.number)):
            return -1
        if name == 'cosine':
            norm_x = np.linalg.norm(x_arr)
            norm_y = np.linalg.norm(y_arr)
            if norm_x == 0 or norm_y == 0:
                return -1
        if name == 'euclidean':
            distance = np.sqrt(np.sum((x_arr - y_arr) ** 2))
        elif name == 'manhattan':
            distance = np.sum(np.abs(x_arr - y_arr))
        elif name == 'chebyshev':
            distance = np.max(np.abs(x_arr - y_arr))
        elif name == 'cosine':
            dot_product = np.dot(x_arr, y_arr)
            norm_product = np.linalg.norm(x_arr) * np.linalg.norm(y_arr)
            distance = 1 - dot_product / norm_product
        else:
            return -1
        return round(distance, 4)
    return d

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function must follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def calculate_score(hand):
        basic_sum = sum(hand)
        ace_present = 1 in hand
        if ace_present and basic_sum + 10 <= 21:
            return basic_sum + 10
        else:
            return basic_sum
    p_score = calculate_score(player)
    d_score = calculate_score(dealer)
    if p_score > 21:
        return -1.0
    if d_score > 21:
        return 1.0
    if p_score > d_score:
        if len(player) == 2 and p_score == 21:
            return 1.5
        else:
            return 1.0
    elif p_score == d_score:
        return 0.0
    else:
        return -1.0

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if type(tree_a) != type(tree_b):
        return False
    if isinstance(tree_a, Leaf):
        return np.allclose(tree_a.value, tree_b.value, atol=1e-08, rtol=0)
    else:
        if tree_a.feature != tree_b.feature:
            return False
        if not np.allclose(tree_a.threshold, tree_b.threshold, atol=1e-08, rtol=0):
            return False
