def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function must return the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """
    if fit_intercept:
        X = np.hstack([np.ones((X.shape[0], 1)), X])
        X_new = np.hstack([np.ones((X_new.shape[0], 1)), X_new])
    D = X.shape[1]
    if isinstance(mu, (float, int)):
        mu_vec = np.full(D, mu, dtype=np.float64)
    else:
        if mu.shape[0] != D:
            raise ValueError(f'mu must be of length {D}, got {mu.shape[0]}')
        mu_vec = mu.astype(np.float64)
    if V is None:
        V_matrix = np.eye(D)
    elif isinstance(V, (float, int)):
        V_matrix = V * np.eye(D)
    elif isinstance(V, np.ndarray):
        if V.ndim == 1:
            if V.shape[0] != D:
                raise ValueError(f'V 1D array must have length {D}, got {V.shape[0]}')
            V_matrix = np.diag(V)
        else:
            if V.shape != (D, D):
                raise ValueError(f'V must be a {D}x{D} matrix, got {V.shape}')
            V_matrix = V.astype(np.float64)
    else:
        raise TypeError('V must be a scalar, 1D array, 2D array, or None')
    inv_V = np.linalg.inv(V_matrix)
    XT_X = X.T @ X
    inv_V_plus_XT_X = inv_V + XT_X
    Sigma_p = np.linalg.inv(inv_V_plus_XT_X)
    term = inv_V @ mu_vec + X.T @ y
    mu_p = Sigma_p @ term
    y_pred = X_new @ mu_p
    return [round(val, 4) for val in y_pred.tolist()]

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in {0, 1, 2}:
        return -1
    x_arr = np.asarray(x)
    sigma = 1 / (1 + np.exp(-x_arr))
    if order == 0:
        result = sigma
    elif order == 1:
        result = sigma * (1 - sigma)
    else:
        result = sigma * (1 - sigma) * (1 - 2 * sigma)
    result_rounded = np.round(result, 4)
    if isinstance(x, (int, float)):
        return float(result_rounded)
    else:
        if result_rounded.ndim == 0:
            result_rounded = result_rounded.reshape(1)
        return result_rounded.tolist()

