min_sup = 0.5
def apriori_frequent_itemsets(transactions: List[List[Hashable]], min_sup: float) -> List[Tuple[Hashable]]:
    """
    Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : List[List[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    List[Tuple[Hashable]]
        Frequent itemsets ordered by length and then lexicographically.
    """
    N = len(transactions)
    if N == 0:
        return []
    transactions_sets = [set(transaction) for transaction in transactions]
    items = set()
    for transaction in transactions:
        for item in transaction:
            items.add(item)
    items = sorted(items)
    item_counts = defaultdict(int)
    for t in transactions_sets:
        for item in t:
            item_counts[item] += 1
    frequent_itemsets = []
    L1 = []
    for item in items:
        if item_counts[item] / N >= min_sup:
            L1.append((item,))
    if L1:
        frequent_itemsets.append(L1)
    k = 2
    while True:
        if k - 2 >= len(frequent_itemsets):
            break
        prev_L = frequent_itemsets[k - 2]
        if not prev_L:
            break
        candidates = []
        for i in range(len(prev_L)):
            for j in range(i + 1, len(prev_L)):
                if prev_L[i][:-1] == prev_L[j][:-1]:
                    candidate = prev_L[i] + (prev_L[j][-1],)
                    candidates.append(candidate)
        prev_freq_set = set(prev_L)
        pruned_candidates = []
        for candidate in candidates:
            is_valid = True
            for i in range(len(candidate)):
                subset = candidate[:i] + candidate[i + 1:]
                if subset not in prev_freq_set:
                    is_valid = False
                    break
            if is_valid:
                pruned_candidates.append(candidate)
        pruned_candidates = sorted(pruned_candidates)
        current_L = []
        for candidate in pruned_candidates:
            count = 0
            for t in transactions_sets:
                if all((item in t for item in candidate)):
                    count += 1
            if count / N >= min_sup:
                current_L.append(candidate)
        if not current_L:
            break
        frequent_itemsets.append(current_L)
        k += 1
    result = []
    for level in frequent_itemsets:
        result.extend(level)
    return result

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """
    Compute the Radial Basis Function (RBF) kernel matrix.

    Parameters:
    - X: List of N feature vectors (N × C).
    - Y: Optional list of M feature vectors (M × C). If None, Y = X.
    - sigma: Optional scale parameter. Can be a float (isotropic), a list of floats (anisotropic), or None.

    Returns:
    - A nested list representing the RBF kernel matrix of shape (N, M).
    - Returns -1 if any input is invalid.
    """
    if not X:
        return -1
    n_features = len(X[0])
    for vec in X:
        if len(vec) != n_features:
            return -1
    if Y is None:
        Y = X
    else:
        if not Y:
            return -1
        for vec in Y:
            if len(vec) != n_features:
                return -1
    C = n_features
    if sigma is None:
        sigma_val = np.sqrt(C / 2.0)
    elif isinstance(sigma, (int, float)):
        sigma_float = float(sigma)
        if sigma_float <= 0:
            return -1
        sigma_val = sigma_float
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        if any((s <= 0 for s in sigma)):
            return -1
        sigma_val = np.array(sigma, dtype=float)
    else:
        return -1
    X_np = np.array(X, dtype=float)
    Y_np = np.array(Y, dtype=float)
    diff = X_np[:, np.newaxis, :] - Y_np[np.newaxis, :, :]
    squared_diff = diff ** 2
    if isinstance(sigma_val, np.ndarray):
        sigma_sq = sigma_val ** 2
        sum_kernel = np.sum(squared_diff / sigma_sq, axis=-1)
    else:
        sigma_sq = sigma_val ** 2
        sum_kernel = np.sum(squared_diff, axis=-1) / sigma_sq
    exponent = -0.5 * sum_kernel
    kernel_matrix = np.exp(exponent)
    kernel_matrix = np.round(kernel_matrix, 4)
    return kernel_matrix.tolist()
