from collections import Counter
def decision_stump(X: list[list[float]], y: list[int]) -> tuple:
    X_np = np.array(X)
    y_np = np.array(y)
    (n_samples, n_features) = X_np.shape

    def compute_entropy(labels):
        if len(labels) == 0:
            return 0.0
        counts = np.unique(labels, return_counts=True)[1]
        probabilities = counts / len(labels)
        return -np.sum(probabilities * np.log2(probabilities))

    def get_majority(labels):
        counts = Counter(labels)
        return max(counts.keys(), key=lambda label: (counts[label], -label))
    parent_entropy = compute_entropy(y_np)
    best_gain = -float('inf')
    best_feature = -1
    best_threshold = None
    best_left_label = None
    best_right_label = None
    for j in range(n_features):
        feature_values = X_np[:, j]
        sorted_unique = np.unique(feature_values)
        if len(sorted_unique) < 2:
            continue
        thresholds = [(sorted_unique[i] + sorted_unique[i + 1]) / 2 for i in range(len(sorted_unique) - 1)]
        for threshold in thresholds:
            mask = feature_values <= threshold
            left_y = y_np[mask]
            right_y = y_np[~mask]
            h_left = compute_entropy(left_y)
            h_right = compute_entropy(right_y)
            n_left = len(left_y)
            n_right = len(right_y)
            gain = parent_entropy - (n_left / n_samples * h_left + n_right / n_samples * h_right)
            if gain > best_gain:
                best_gain = gain
                best_feature = j
                best_threshold = threshold
                best_left_label = get_majority(left_y)
                best_right_label = get_majority(right_y)
            elif gain == best_gain:
                if j < best_feature:
                    best_feature = j
                    best_threshold = threshold
                    best_left_label = get_majority(left_y)
                    best_right_label = get_majority(right_y)
                elif j == best_feature:
                    if threshold < best_threshold:
                        best_threshold = threshold
                        best_left_label = get_majority(left_y)
                        best_right_label = get_majority(right_y)
    if best_gain <= 0:
        majority_label = get_majority(y_np)
        return (-1, None, majority_label, majority_label)
    else:
        return (best_feature, round(best_threshold, 4), best_left_label, best_right_label)

