import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    freq = Counter()
    for t in transactions:
        for item in t:
            freq[item] += 1
    frequent_items = {item for (item, count) in freq.items() if count >= min_sup}

    def sort_transaction(t):
        return sorted([item for item in t if item in frequent_items], key=lambda x: (-freq[x], x))
    sorted_transactions = [sort_transaction(t) for t in transactions]

    class FPNode:

        def __init__(self, item, count, parent):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = dict()
            self.node_link = None

    def build_fp_tree(transactions):
        header = defaultdict(lambda : None)
        root = FPNode(None, 0, None)
        for t in transactions:
            if not t:
                continue
            current = root
            for item in t:
                if item in current.children:
                    current.children[item].count += 1
                else:
                    new_node = FPNode(item, 1, current)
                    current.children[item] = new_node
                    if header[item] is None:
                        header[item] = new_node
                    else:
                        node = header[item]
                        while node.node_link is not None:
                            node = node.node_link
                        node.node_link = new_node
                current = current.children[item]
        return (header, root)
    (header_table, root) = build_fp_tree(sorted_transactions)

    def mine_tree(header_table, root, prefix, frequent_itemsets):
        if not header_table:
            return
        items = []
        for item in header_table:
            node = header_table[item]
            total = 0
            while node:
                total += node.count
                node = node.node_link
            items.append((item, total))
        items.sort(key=lambda x: (x[1], x[0]))
        for (item, _) in items:
            new_prefix = prefix + (item,)
            frequent_itemsets.append(new_prefix)
            cond_patterns = []
            node = header_table[item]
            while node:
                path = []
                count = node.count
                current = node.parent
                while current.item is not None:
                    path.append(current.item)
                    current = current.parent
                path.reverse()
                cond_patterns.append((path, count))
                node = node.node_link
            cond_freq = Counter()
            for (path, count) in cond_patterns:
                for p in path:
                    cond_freq[p] += count
            cond_frequent_items = {p for p in cond_freq if cond_freq[p] >= min_sup}
            if not cond_frequent_items:
                continue
            sorted_cond_patterns = []
            for (path, count) in cond_patterns:
                filtered_path = [p for p in path if p in cond_frequent_items]
                filtered_path.sort(key=lambda x: (-cond_freq[x], x))
                if filtered_path:
                    sorted_cond_patterns.append((filtered_path, count))
            cond_header = defaultdict(lambda : None)
            cond_root = FPNode(None, 0, None)
            for (path, count) in sorted_cond_patterns:
                current = cond_root
                for p in path:
                    if p in current.children:
                        current.children[p].count += count
                    else:
                        new_node = FPNode(p, count, current)
                        current.children[p] = new_node
                        if cond_header[p] is None:
                            cond_header[p] = new_node
                        else:
                            node = cond_header[p]
                            while node.node_link is not None:
                                node = node.node_link
                            node.node_link = new_node
                    current = current.children[p]
            if cond_header:
                mine_tree(cond_header, cond_root, new_prefix, frequent_itemsets)
    frequent_itemsets = []
    mine_tree(header_table, root, (), frequent_itemsets)
    sorted_itemsets = [tuple(sorted(itemset)) for itemset in frequent_itemsets]
    sorted_itemsets.sort(key=lambda x: (len(x), x))
    return sorted_itemsets
