import numpy as np
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """
    Tiny Isolation Forest implementation for outlier detection.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if n_samples == 1:
        return [0] if epsilon > 0.0 else []
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples

    def c(n):
        if n <= 1:
            return 0.0
        else:
            return 2.0 * (math.log(n - 1) + 0.5772156649) - 2.0 * (n - 1) / n

    def build_tree(data_subset, current_depth, max_depth):
        n = data_subset.shape[0]
        if n <= 1 or current_depth == max_depth:
            return {'is_leaf': True, 'count': n}
        feature = np.random.randint(0, n_features)
        col = data_subset[:, feature]
        (min_val, max_val) = (np.min(col), np.max(col))
        if min_val == max_val:
            return {'is_leaf': True, 'count': n}
        threshold = np.random.uniform(min_val, max_val)
        left_mask = data_subset[:, feature] <= threshold
        left_data = data_subset[left_mask]
        right_data = data_subset[~left_mask]
        left_child = build_tree(left_data, current_depth + 1, max_depth)
        right_child = build_tree(right_data, current_depth + 1, max_depth)
        return {'is_leaf': False, 'feature': feature, 'threshold': threshold, 'left': left_child, 'right': right_child}

    def get_path_length(tree, x, current_depth=0):
        if tree['is_leaf']:
            return current_depth + c(tree['count'])
        else:
            feature = tree['feature']
            threshold = tree['threshold']
            if x[feature] <= threshold:
                return get_path_length(tree['left'], x, current_depth + 1)
            else:
                return get_path_length(tree['right'], x, current_depth + 1)
    trees = []
    for _ in range(n_trees):
        if sample_size >= n_samples:
            subsample_data = data
        else:
            indices = np.random.choice(n_samples, sample_size, replace=False)
            subsample_data = data[indices]
        subsample_size = subsample_data.shape[0]
        height_limit = math.ceil(math.log2(subsample_size))
        tree = build_tree(subsample_data, 0, height_limit)
        trees.append(tree)
    avg_path_lengths = np.zeros(n_samples)
    for tree in trees:
        for i in range(n_samples):
            path_length = get_path_length(tree, data[i])
            avg_path_lengths[i] += path_length
    avg_path_lengths /= n_trees
    scores = np.zeros(n_samples)
    for i in range(n_samples):
        h_x = avg_path_lengths[i]
        scores[i] = 2 ** (-h_x / phi)
    if epsilon == 0.0:
        return []
    n_outliers = math.ceil(epsilon * n_samples)
    indexed_scores = list(zip(scores, range(n_samples)))
    indexed_scores.sort(key=lambda x: (-x[0], x[1]))
    selected = indexed_scores[:n_outliers]
    outlier_indices = [idx for (score, idx) in selected]
    outlier_indices.sort()
    return outlier_indices
