import numpy as np
X_train = np.array([[0, 1], [1, 0]])
y_train = np.array([0, 1])
X_test = np.array([[0, 1]])
predictions = maxent_predict(X_train, y_train, X_test)
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """Implements a Maximum Entropy classifier using Generalized Iterative Scaling (GIS)."""
    col_vals = defaultdict(set)
    for x in X_train:
        for (col, val) in enumerate(x):
            col_vals[col].add(val)
    possible_labels = np.unique(y_train)
    features = []
    for col in col_vals:
        for val in col_vals[col]:
            for label in possible_labels:
                features.append((col, val, label))
    feature_index_map = {feature: idx for (idx, feature) in enumerate(features)}
    n_features = len(features)
    x_list_per_feature = []
    for feature in features:
        (col, val, label) = feature
        x_list = []
        for (x_idx, x) in enumerate(X_train):
            if x[col] == val:
                x_list.append(x_idx)
        x_list_per_feature.append(x_list)
    empirical_counts = np.zeros(n_features, dtype=int)
    for (feature_idx, (col, val, label)) in enumerate(features):
        count = 0
        for x_idx in x_list_per_feature[feature_idx]:
            if y_train[x_idx] == label:
                count += 1
        empirical_counts[feature_idx] = count
    feature_indices_per_x_y = []
    for x_idx in range(len(X_train)):
        x = X_train[x_idx]
        x_dict = {}
        for label in possible_labels:
            features_for_label = []
            for col in range(X_train.shape[1]):
                val = x[col]
                feature = (col, val, label)
                if feature in feature_index_map:
                    features_for_label.append(feature_index_map[feature])
            x_dict[label] = features_for_label
        feature_indices_per_x_y.append(x_dict)
    weights = np.zeros(n_features)
    for _ in range(n_iter):
        scores_per_x = []
        for x_idx in range(len(X_train)):
            label_to_score = {}
            for label in possible_labels:
                features = feature_indices_per_x_y[x_idx][label]
                score = sum((weights[feature_idx] for feature_idx in features))
                label_to_score[label] = score
            scores_per_x.append(label_to_score)
        probabilities = []
        for x_idx in range(len(X_train)):
            label_to_score = scores_per_x[x_idx]
            labels = list(label_to_score.keys())
            scores = np.array([label_to_score[label] for label in labels])
            exp_scores = np.exp(scores - np.max(scores))
            prob_sum = np.sum(exp_scores)
            label_to_prob = {label: exp_score / prob_sum for (label, exp_score) in zip(labels, exp_scores)}
            probabilities.append(label_to_prob)
        expected_counts = np.zeros(n_features)
        for feature_idx in range(n_features):
            (col, val, label_j) = features[feature_idx]
            x_list = x_list_per_feature[feature_idx]
            total = 0.0
            for x_idx in x_list:
                if label_j in probabilities[x_idx]:
                    total += probabilities[x_idx][label_j]
            expected_counts[feature_idx] = total
        epsilon_val = 1e-12
        ratios = (empirical_counts + epsilon_val) / (expected_counts + epsilon_val)
        log_ratios = np.log(ratios)
        delta = np.mean(log_ratios)
        weights += delta
        if abs(delta) < epsilon:
            break
    predictions = []
    for x in X_test:
        best_score = -np.inf
        best_label = None
        for label in possible_labels:
            score = 0.0
            for col in range(X_test.shape[1]):
                val = x[col]
                feature = (col, val, label)
                if feature in feature_index_map:
                    feature_idx = feature_index_map[feature]
                    score += weights[feature_idx]
            if score > best_score:
                best_score = score
                best_label = label
        predictions.append(best_label)
    return predictions
