def calculate_brightness(img):
    if not img or not img[0]:
        return -1
    rows = len(img)
    cols = len(img[0])
    for row in img:
        if len(row) != cols:
            return -1
    total_pixels = 0
    brightness_sum = 0
    for row in img:
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
            brightness_sum += pixel
            total_pixels += 1
    average_brightness = brightness_sum / total_pixels
    return round(average_brightness, 2)
