def find_treasure(start_x: float, learning_rate: float=0.1, tolerance: float=1e-06, max_iters: int=10000) -> float:

    def f(x):
        return x ** 4 - 3 * x ** 3 + 2

    def df(x):
        return 4 * x ** 3 - 9 * x ** 2
    x = start_x
    for i in range(max_iters):
        grad = df(x)
        if abs(grad) < tolerance:
            break
        x -= learning_rate * grad
    return round(x, 4)
