def entropy(class_counts: Dict[Any, int]) -> float:
    """Calculate the entropy of a set of class counts."""
    total = sum(class_counts.values())
    if total == 0:
        return 0.0
    return -sum((count / total * math.log2(count / total) for count in class_counts.values()))
def information_gain(parent_entropy: float, subsets: List[Dict[str, Any]], target_attr: str) -> float:
    """Calculate the information gain from splitting into subsets."""
    total = sum((len(subset) for subset in subsets))
    weighted_entropy = 0.0
    for subset in subsets:
        if not subset:
            continue
        class_counts = Counter((example[target_attr] for example in subset))
        subset_entropy = entropy(class_counts)
        weighted_entropy += len(subset) / total * subset_entropy
    return parent_entropy - weighted_entropy
def split_examples(examples: List[Dict[str, Any]], attribute: str, value: Any) -> List[Dict[str, Any]]:
    """Split examples based on the value of a given attribute."""
    return [example for example in examples if example[attribute] == value]
def most_common_class(examples: List[Dict[str, Any]], target_attr: str) -> Any:
    """Return the most common class in the examples."""
    if not examples:
        return None
    class_counts = Counter((example[target_attr] for example in examples))
    return class_counts.most_common(1)[0][0]
def learn_decision_tree(examples: List[Dict[str, Any]], attributes: List[str], target_attr: str) -> Dict[str, Any]:
    """Learn a decision tree using entropy and information gain."""
    if not examples:
        return most_common_class(examples, target_attr)
    class_values = [example[target_attr] for example in examples]
    if len(set(class_values)) == 1:
        return class_values[0]
    if not attributes:
        return most_common_class(examples, target_attr)
    best_attr = None
    max_gain = -1
    parent_entropy = entropy(Counter((example[target_attr] for example in examples)))
    for attr in attributes:
        if attr == target_attr:
            continue
        values = set((example[attr] for example in examples))
        subsets = [split_examples(examples, attr, value) for value in values]
        gain = information_gain(parent_entropy, subsets, target_attr)
        if gain > max_gain:
            max_gain = gain
            best_attr = attr
    if best_attr is None:
        return most_common_class(examples, target_attr)
    tree = {best_attr: {}}
    remaining_attributes = [attr for attr in attributes if attr != best_attr]
    for value in set((example[best_attr] for example in examples)):
        subtree = learn_decision_tree(split_examples(examples, best_attr, value), remaining_attributes, target_attr)
        tree[best_attr][value] = subtree
    return tree

import numpy as np
def train_neuron(features: np.ndarray, labels: np.ndarray, initial_weights: np.ndarray, initial_bias: float, learning_rate: float, epochs: int) -> (np.ndarray, float, list[float]):
    weights = initial_weights.copy()
    bias = initial_bias
    mse_values = []

    def sigmoid(x):
        return 1 / (1 + np.exp(-x))

    def sigmoid_derivative(x):
        return x * (1 - x)

    def mse(y_true, y_pred):
        return np.mean((y_true - y_pred) ** 2)
    for epoch in range(epochs):
        z = np.dot(features, weights) + bias
        y_pred = sigmoid(z)
        loss = mse(labels, y_pred)
        mse_values.append(round(loss, 4))
        error = y_pred - labels
        d_z = error * sigmoid_derivative(y_pred)
        grad_weights = np.dot(features.T, d_z) / features.shape[0]
        grad_bias = np.mean(d_z)
        weights -= learning_rate * grad_weights
        bias -= learning_rate * grad_bias
    weights = np.round(weights, 4)
    bias = round(bias, 4)
    mse_values = [round(mse, 4) for mse in mse_values]
    return (weights.tolist(), bias, mse_values)

class Value:

    def __init__(self, data, _children=(), _op=''):
        self.data = data
        self.grad = 0
        self._backward = lambda : None
        self._prev = set(_children)
        self._op = _op

    def __add__(self, other):
        other = other if isinstance(other, Value) else Value(other)
        out = Value(self.data + other.data, _children=(self, other), _op='add')

        def _backward():
            self.grad += out.grad * 1
            other.grad += out.grad * 1
        out._backward = _backward
        return out

    def __mul__(self, other):
        other = other if isinstance(other, Value) else Value(other)
        out = Value(self.data * other.data, _children=(self, other), _op='mul')

        def _backward():
            self.grad += out.grad * other.data
            other.grad += out.grad * self.data
        out._backward = _backward
        return out

    def relu(self):
        out = Value(self.data if self.data > 0 else 0, _children=(self,), _op='relu')

        def _backward():
            self.grad += (out.data > 0) * out.grad
        out._backward = _backward
        return out

    def backward(self):
        topo = []
        visited = set()

        def build_topo(v):
            if v not in visited:
                visited.add(v)
                for child in v._prev:
                    build_topo(child)
                topo.append(v)
        build_topo(self)
        self.grad = 1.0
        for node in reversed(topo):
            node._backward()

    def __neg__(self):
        return self * -1

    def __radd__(self, other):
        return self + other

    def __sub__(self, other):
        return self + -other

    def __rsub__(self, other):
        return other + -self

    def __rmul__(self, other):
        return self * other

    def __truediv__(self, other):
        return self * other ** (-1)

    def __rtruediv__(self, other):
        return other * self ** (-1)

    def __pow__(self, other):
        assert isinstance(other, (int, float)), 'Only int/float exponents are supported'
        out = Value(self.data ** other, _children=(self,), _op=f'**{other}')

        def _backward():
            self.grad += out.grad * other * self.data ** (other - 1)
        out._backward = _backward
        return out

    def __repr__(self):
        return f'Value(data={self.data}, grad={self.grad})'

