def rref(matrix):
    """
    Convert a matrix to its Reduced Row Echelon Form (RREF).

    Parameters:
    matrix (list of lists or np.ndarray): The input matrix.

    Returns:
    list: The RREF of the matrix as a Python list.
    """
    mat = np.array(matrix, dtype=float)
    (rows, cols) = mat.shape
    lead = 0
    for r in range(rows):
        if lead >= cols:
            return mat.tolist()
        i = r
        while mat[i, lead] == 0:
            i += 1
            if i == rows:
                i = r
                lead += 1
                if lead == cols:
                    return mat.tolist()
        mat[[i, r]] = mat[[r, i]]
        mat[r] = mat[r] / mat[r, lead]
        for i in range(rows):
            if i != r and mat[i, lead] != 0:
                mat[i] = mat[i] - mat[i, lead] * mat[r]
        lead += 1
    return mat.tolist()

