def OSA(source: str, target: str) -> int:
    m = len(source)
    n = len(target)
    dp = np.zeros((m + 1, n + 1), dtype=int)
    for i in range(m + 1):
        dp[i][0] = i
    for j in range(n + 1):
        dp[0][j] = j
    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if source[i - 1] == target[j - 1]:
                dp[i][j] = dp[i - 1][j - 1]
            else:
                substitution = dp[i - 1][j - 1] + 1
                insertion = dp[i][j - 1] + 1
                deletion = dp[i - 1][j] + 1
                transposition = dp[i - 2][j - 2] + 1 if i >= 2 and j >= 2 and (source[i - 2] == target[j - 1]) and (source[i - 1] == target[j - 2]) else float('inf')
                dp[i][j] = min(substitution, insertion, deletion, transposition)
    return dp[m][n]

import numpy as np
def compute_tf_idf(corpus, query):
    if not corpus:
        raise ValueError('The corpus is empty. Cannot compute TF-IDF.')
    vocabulary = set()
    for doc in corpus:
        vocabulary.update(doc)
    vocabulary.update(query)
    vocabulary = list(vocabulary)
    vocab_size = len(vocabulary)
    word_to_index = {word: i for (i, word) in enumerate(vocabulary)}
    num_docs = len(corpus)
    tf = np.zeros((num_docs, vocab_size))
    df = np.zeros(vocab_size)
    for (doc_idx, doc) in enumerate(corpus):
        word_count = {}
        for word in doc:
            word_count[word] = word_count.get(word, 0) + 1
        total_words = len(doc)
        for (word, count) in word_count.items():
            tf[doc_idx, word_to_index[word]] = count / total_words
    for doc in corpus:
        unique_words = set(doc)
        for word in unique_words:
            df[word_to_index[word]] += 1
    idf = np.log((num_docs + 1) / (df + 1)) + 1
    tf_idf = tf * idf
    query_indices = [word_to_index[word] for word in query if word in word_to_index]
    if not query_indices:
        return []
    tf_idf_query = tf_idf[:, query_indices]
    tf_idf_rounded = np.round(tf_idf_query, 4)
    return tf_idf_rounded.tolist()

def compressed_row_sparse_matrix(dense_matrix):
    """
    Convert a dense matrix to its Compressed Row Sparse (CSR) representation.

    :param dense_matrix: 2D list representing a dense matrix
    :return: A tuple containing (values array, column indices array, row pointer array)
    """
    if not dense_matrix or not dense_matrix[0]:
        return ([], [], [0])
    values = []
    column_indices = []
    row_pointer = [0]
    for row in dense_matrix:
        count = 0
        for (col_idx, val) in enumerate(row):
            if val != 0:
                values.append(val)
                column_indices.append(col_idx)
                count += 1
        row_pointer.append(row_pointer[-1] + count)
    return (values, column_indices, row_pointer)

def compressed_col_sparse_matrix(dense_matrix):
    """
    Convert a dense matrix into its Compressed Column Sparse (CSC) representation.

    :param dense_matrix: List of lists representing the dense matrix
    :return: Tuple of (values, row indices, column pointer)
    """
    if not dense_matrix or not dense_matrix[0]:
        return ([], [], [0])
    num_rows = len(dense_matrix)
    num_cols = len(dense_matrix[0])
    values = []
    row_indices = []
    col_ptr = [0]
    for col in range(num_cols):
        for row in range(num_rows):
            val = dense_matrix[row][col]
            if val != 0:
                values.append(val)
                row_indices.append(row)
        col_ptr.append(len(values))
    return (values, row_indices, col_ptr)

def calculate_brightness(img):
    if not img or not img[0]:
        return -1
    rows = len(img)
    cols = len(img[0])
    for row in img:
        if len(row) != cols:
            return -1
    total_pixels = 0
    brightness_sum = 0
    for row in img:
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
            brightness_sum += pixel
            total_pixels += 1
    average_brightness = brightness_sum / total_pixels
    return round(average_brightness, 2)

