def pos_encoding(position: int, d_model: int):
    if position == 0 or d_model <= 0:
        return -1
    pe = np.zeros((position, d_model), dtype=np.float16)
    for pos in range(position):
        for i in range(0, d_model, 2):
            if i + 1 <= d_model:
                div_term = np.power(10000, 2 * (i // 2) / d_model)
                pe[pos, i] = np.sin(pos / div_term).astype(np.float16)
                pe[pos, i + 1] = np.cos(pos / div_term).astype(np.float16)
    return pe.tolist()

