def softplus(x: float) -> float:
    """
    Compute the softplus activation function.

    Args:
        x: Input value

    Returns:
        The softplus value: log(1 + e^x)
    """
    if x < -30:
        return round(0.0, 4)
    elif x > 30:
        return round(x, 4)
    else:
        return round(math.log(1 + math.exp(x)), 4)

def min_max(x: list[int]) -> list[float]:
    if not x:
        return []
    min_val = min(x)
    max_val = max(x)
    if min_val == max_val:
        return [0.0 for _ in x]
    normalized = [(val - min_val) / (max_val - min_val) for val in x]
    return [round(val, 4) for val in normalized]

