from collections import Counter, defaultdict
from typing import List, Dict, Tuple, Set
class FPTreeNode:

    def __init__(self, name: str, count: int, parent):
        self.name = name
        self.count = count
        self.parent = parent
        self.children = {}
        self.node_link = None

    def increment(self, count: int):
        self.count += count
def build_fp_tree(transactions: List[List[str]], min_support: int) -> Tuple[FPTreeNode, Dict[str, FPTreeNode]]:
    """Build the FP-tree from the transactions."""
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return (None, {})
    sorted_items = sorted(frequent_items, key=lambda x: (-item_counts[x], x))
    item_freq = {item: item_counts[item] for item in sorted_items}
    header_table = {}
    root = FPTreeNode('Null', 1, None)
    for item in sorted_items:
        header_table[item] = None
    for transaction in transactions:
        filtered_transaction = [item for item in transaction if item in item_freq]
        if not filtered_transaction:
            continue
        filtered_transaction.sort(key=lambda x: (-item_freq[x], x))
        update_tree(filtered_transaction, root, header_table, 1)
    return (root, header_table)
def update_tree(items: List[str], node: FPTreeNode, header_table: Dict[str, FPTreeNode], count: int):
    """Recursively update the FP-tree with a transaction."""
    if not items:
        return
    item = items[0]
    if item in node.children:
        node.children[item].increment(count)
    else:
        node.children[item] = FPTreeNode(item, count, node)
        if header_table[item] is None:
            header_table[item] = node.children[item]
        else:
            current = header_table[item]
            while current.node_link is not None:
                current = current.node_link
            current.node_link = node.children[item]
    update_tree(items[1:], node.children[item], header_table, count)
def fp_growth(transactions: List[List[str]], min_support: int) -> List[List[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions or min_support < 1:
        return []
    (root, header_table) = build_fp_tree(transactions, min_support)
    if not header_table:
        return []
    frequent_itemsets = []
    mine_tree(header_table, [], frequent_itemsets, min_support)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
