import numpy as np
import math
from typing import List
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> List[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if epsilon == 0:
        return []

    def c(n):
        if n <= 1:
            return 0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def compute_phi(n):
        return 2 * math.log(n - 1) - 2 * (n - 1) / n

    class IsolationTree:

        def __init__(self, data, height_limit):
            self.height_limit = height_limit
            self.n_features = data.shape[1]
            self.root = self.build_tree(data, 0)

        def build_tree(self, data, depth):
            n = data.shape[0]
            if depth == self.height_limit or n <= 1:
                return {'type': 'leaf', 'size': n}
            else:
                feature_idx = np.random.randint(self.n_features)
                min_val = np.min(data[:, feature_idx])
                max_val = np.max(data[:, feature_idx])
                threshold = np.random.uniform(min_val, max_val)
                left_data = data[data[:, feature_idx] < threshold]
                right_data = data[data[:, feature_idx] >= threshold]
                left = self.build_tree(left_data, depth + 1)
                right = self.build_tree(right_data, depth + 1)
                return {'type': 'node', 'feature': feature_idx, 'threshold': threshold, 'left': left, 'right': right}

        def get_path_length(self, x):
            return self._get_path_length(self.root, x, 0)

        def _get_path_length(self, node, x, depth):
            if node['type'] == 'leaf':
                size = node['size']
                return depth + c(size)
            elif x[node['feature']] < node['threshold']:
                return self._get_path_length(node['left'], x, depth + 1)
            else:
                return self._get_path_length(node['right'], x, depth + 1)

    def compute_height_limit(sample_size):
        return math.ceil(math.log2(sample_size))
    trees = []
    for _ in range(n_trees):
        if sample_size >= n_samples:
            subsample = data
        else:
            indices = np.random.choice(n_samples, size=sample_size, replace=False)
            subsample = data[indices]
        height_limit = compute_height_limit(sample_size)
        tree = IsolationTree(subsample, height_limit)
        trees.append(tree)
    avg_path_lengths = np.zeros(n_samples)
    for i in range(n_samples):
        x = data[i]
        total_path_length = 0
        for tree in trees:
            total_path_length += tree.get_path_length(x)
        avg_path_lengths[i] = total_path_length / n_trees
    phi = compute_phi(n_samples)
    scores = 2 ** (-(avg_path_lengths / phi))
    n_outliers = max(1, int(epsilon * n_samples))
    outlier_indices = np.argsort(scores)[-n_outliers:]
    outlier_indices = np.sort(outlier_indices).tolist()
    return outlier_indices
