def gini_impurity(y: np.ndarray) -> float:
    """Calculate the Gini impurity of a set of class labels."""
    if len(y) == 0:
        return 0.0
    counts = np.bincount(y)
    probabilities = counts / len(y)
    return 1.0 - np.sum(probabilities ** 2)
def best_split(X: np.ndarray, y: np.ndarray) -> Tuple[Optional[int], Optional[float], float]:
    """Find the best feature and threshold to split on, maximizing Gini gain."""
    n_features = X.shape[1]
    best_gini = float('inf')
    (best_idx, best_threshold) = (None, None)
    for idx in range(n_features):
        unique_values = np.unique(X[:, idx])
        thresholds = []
        for i in range(len(unique_values) - 1):
            if unique_values[i] != unique_values[i + 1]:
                thresholds.append((unique_values[i] + unique_values[i + 1]) / 2)
        for threshold in thresholds:
            left_mask = X[:, idx] <= threshold
            right_mask = X[:, idx] > threshold
            if np.sum(left_mask) == 0 or np.sum(right_mask) == 0:
                continue
            gini_left = gini_impurity(y[left_mask])
            gini_right = gini_impurity(y[right_mask])
            weighted_gini = np.sum(left_mask) / len(y) * gini_left + np.sum(right_mask) / len(y) * gini_right
            if weighted_gini < best_gini:
                best_gini = weighted_gini
                best_idx = idx
                best_threshold = threshold
    return (best_idx, best_threshold, best_gini)
class DecisionTreeNode:

    def __init__(self):
        self.feature_index = None
        self.threshold = None
        self.left = None
        self.right = None
        self.label = None
        self.depth = 0

    def is_leaf(self):
        return self.label is not None
def build_tree(X: np.ndarray, y: np.ndarray, depth: int, max_depth: Optional[int]) -> DecisionTreeNode:
    """Recursively build the decision tree."""
    node = DecisionTreeNode()
    node.depth = depth
    if np.all(y == y[0]):
        node.label = y[0]
        return node
    if max_depth is not None and depth >= max_depth:
        node.label = np.argmax(np.bincount(y))
        return node
    (feature_index, threshold, best_gini) = best_split(X, y)
    if feature_index is None:
        node.label = np.argmax(np.bincount(y))
        return node
    node.feature_index = feature_index
    node.threshold = threshold
    left_mask = X[:, feature_index] <= threshold
    right_mask = X[:, feature_index] > threshold
    node.left = build_tree(X[left_mask], y[left_mask], depth + 1, max_depth)
    node.right = build_tree(X[right_mask], y[right_mask], depth + 1, max_depth)
    return node
def predict_tree(node: DecisionTreeNode, x: np.ndarray) -> int:
    """Predict the class label for a single sample using the decision tree."""
    if node.is_leaf():
        return node.label
    if x[node.feature_index] <= node.threshold:
        return predict_tree(node.left, x)
    else:
        return predict_tree(node.right, x)
