import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate(x, y):
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return False
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except:
            return False
        if x.ndim != 1 or y.ndim != 1:
            return False
        if x.size == 0 or y.size == 0 or x.size != y.size:
            return False
        return (x, y)

    def euclidean(x, y):
        (x, y) = validate(x, y)
        if not isinstance(x, np.ndarray):
            return -1
        return round(np.sqrt(np.sum((x - y) ** 2)), 4)

    def manhattan(x, y):
        (x, y) = validate(x, y)
        if not isinstance(x, np.ndarray):
            return -1
        return round(np.sum(np.abs(x - y)), 4)

    def chebyshev(x, y):
        (x, y) = validate(x, y)
        if not isinstance(x, np.ndarray):
            return -1
        return round(np.max(np.abs(x - y)), 4)

    def cosine(x, y):
        (x, y) = validate(x, y)
        if not isinstance(x, np.ndarray):
            return -1
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        return round(1 - np.dot(x, y) / (norm_x * norm_y), 4)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    if name not in metrics:
        return lambda x, y: -1
    return metrics[name]

import numpy as np
def leaky_relu(z, a=0.01):
    """Apply the Leaky ReLU activation to every element in *z*.

    Args:
        z: A scalar, list (possibly nested) or ``numpy.ndarray`` of numbers.
        a: Optional float in [0,1) — the slope for negative inputs. Defaults to 0.01.

    Returns:
        A Python list with the same structure as *z* where each value has been transformed
        by the Leaky ReLU activation.
    """

    def _leaky_relu(x):
        if isinstance(x, (int, float)):
            return x if x >= 0 else a * x
        elif isinstance(x, list):
            return [_leaky_relu(val) for val in x]
        elif isinstance(x, np.ndarray):
            return (x * (x >= 0) + a * x * (x < 0)).tolist()
        else:
            raise TypeError('Unsupported input type: {}'.format(type(x)))
    return _leaky_relu(z)

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """Performs forward and backward passes of a 2-D average-pooling layer.

    The function must return a tuple (pooled_output, grad_input), both rounded
    to the nearest 4-th decimal and converted to regular Python lists using
    `tolist()`.
    """
    (N, C, H, W) = X.shape
    (p_h, p_w) = pool_shape
    (s_h, s_w) = stride
    out_h = (H - p_h) // s_h + 1
    out_w = (W - p_w) // s_w + 1
    pooled_output = np.zeros((N, C, out_h, out_w))
    for n in range(N):
        for c in range(C):
            for i in range(out_h):
                for j in range(out_w):
                    h_start = i * s_h
                    w_start = j * s_w
                    window = X[n, c, h_start:h_start + p_h, w_start:w_start + p_w]
                    pooled_output[n, c, i, j] = np.mean(window)
    grad_input = np.zeros_like(X)
    for n in range(N):
        for c in range(C):
            for i in range(out_h):
                for j in range(out_w):
                    h_start = i * s_h
                    w_start = j * s_w
                    grad = accum_grad[n, c, i, j] / (p_h * p_w)
                    grad_input[n, c, h_start:h_start + p_h, w_start:w_start + p_w] += grad
    pooled_output = np.round(pooled_output, 4).tolist()
    grad_input = np.round(grad_input, 4).tolist()
    return (pooled_output, grad_input)

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_space(space: Space, is_composite: bool) -> bool:
        if is_composite:
            if isinstance(space, (Tuple, Dict)):
                return all((isinstance(sub_space, Box) for sub_space in space.spaces.values()))
            else:
                return False
        else:
            return isinstance(space, Box)
    cont_action = check_space(env.action_space, tuple_action)
    cont_obs = check_space(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Calculate the Root Mean Squared Logarithmic Error (RMSLE)."""
    if len(actual) != len(predicted):
        return -1
    if any((x < 0 for x in actual)) or any((x < 0 for x in predicted)):
        return -1
    n = len(actual)
    if n == 0:
        return -1
    log_actual = np.log1p(actual)
    log_predicted = np.log1p(predicted)
    squared_errors = (log_predicted - log_actual) ** 2
    mean_squared_log_error = np.mean(squared_errors)
    rmsle = np.sqrt(mean_squared_log_error)
    return round(rmsle, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in {0, 1, 2}:
        return -1
    x = np.array(x, dtype=float)
    sig = 1 / (1 + np.exp(-x))
    if order == 0:
        result = sig
    elif order == 1:
        result = sig * (1 - sig)
    elif order == 2:
        result = sig * (1 - sig) * (1 - 2 * sig)
    result = np.round(result, 4)
    if x.ndim == 0:
        return float(result)
    else:
        return result.tolist()

import itertools
from collections import defaultdict
from typing import List, Tuple, Hashable
def apriori_frequent_itemsets(transactions: List[List[Hashable]], min_sup: float) -> List[Tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    N = len(transactions)
    C1 = defaultdict(int)
    for t in transactions:
        for item in set(t):
            C1[frozenset([item])] += 1
    L = []
    L1 = {item for (item, count) in C1.items() if count / N >= min_sup}
    L.append(L1)

    def generate_candidates(prev_itemsets, k):
        candidates = set()
        for itemset1 in prev_itemsets:
            for itemset2 in prev_itemsets:
                if list(itemset1)[:-1] == list(itemset2)[:-1] and list(itemset1)[-1] < list(itemset2)[-1]:
                    union = itemset1 | itemset2
                    if len(union) == k:
                        candidates.add(union)
        return candidates

    def prune_candidates(candidates, prev_itemsets):
        pruned = set()
        for candidate in candidates:
            for subset in itertools.combinations(candidate, k - 1):
                if frozenset(subset) not in prev_itemsets:
                    break
            else:
                pruned.add(candidate)
        return pruned
    k = 2
    while True:
        Ck = generate_candidates(L[k - 2], k)
        Ck = prune_candidates(Ck, L[k - 2])
        Ck_support = defaultdict(int)
        for t in transactions:
            t_set = frozenset(t)
            for candidate in Ck:
                if candidate.issubset(t_set):
                    Ck_support[candidate] += 1
        Lk = {itemset for (itemset, count) in Ck_support.items() if count / N >= min_sup}
        if not Lk:
            break
        L.append(Lk)
        k += 1
    result = []
    for itemset_list in L:
        for itemset in itemset_list:
            sorted_itemset = tuple(sorted(itemset))
            result.append(sorted_itemset)
    result.sort(key=lambda x: (len(x), x))
    return result
