def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """Train a MaxEnt classifier using GIS and predict labels for test data."""
    feature_index = {}
    feature_counter = 0
    label_set = np.unique(y_train)
    for i in range(X_train.shape[0]):
        x = X_train[i]
        y = y_train[i]
        for j in range(x.shape[0]):
            val = x[j]
            if val != 0:
                key = (j, val, y)
                if key not in feature_index:
                    feature_index[key] = feature_counter
                    feature_counter += 1
    n_features = len(feature_index)
    n_labels = len(label_set)
    empirical_counts = np.zeros(n_features)
    for i in range(X_train.shape[0]):
        x = X_train[i]
        y = y_train[i]
        for j in range(x.shape[0]):
            val = x[j]
            if val != 0:
                key = (j, val, y)
                if key in feature_index:
                    idx = feature_index[key]
                    empirical_counts[idx] += 1
    empirical_counts /= X_train.shape[0]
    weights = np.zeros(n_features)
    for _ in range(n_iter):
        model_counts = np.zeros(n_features)
        for i in range(X_train.shape[0]):
            x = X_train[i]
            y_true = y_train[i]
