def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Implement k-Nearest Neighbors classifier."""

    def euclidean_distance(x1, x2):
        return np.sqrt(np.sum((x1 - x2) ** 2, axis=1))

    def manhattan_distance(x1, x2):
        return np.sum(np.abs(x1 - x2), axis=1)

    def cosine_distance(x1, x2):
        dot_product = np.sum(x1 * x2, axis=1)
        norm_x1 = np.sqrt(np.sum(x1 ** 2, axis=1))
        norm_x2 = np.sqrt(np.sum(x2 ** 2, axis=1))
        return 1 - dot_product / (norm_x1 * norm_x2)
    if metric == 'manhattan':
        distance_func = manhattan_distance
    elif metric == 'cosine':
        distance_func = cosine_distance
    else:
        distance_func = euclidean_distance
    distances = np.array([distance_func(X_train, x) for x in X_test])
    nearest_indices = np.argsort(distances, axis=1)[:, :k]
    y_pred = []
    for indices in nearest_indices:
        labels = y_train[indices]
        (values, counts) = np.unique(labels, return_counts=True)
        max_count = np.max(counts)
        y_pred.append(values[np.where(counts == max_count)[0][0]])
    return np.array(y_pred)
