def gini_impurity(y):
    """Calculate the Gini impurity for a set of labels."""
    (_, counts) = np.unique(y, return_counts=True)
    probabilities = counts / len(y)
    return 1 - np.sum(probabilities ** 2)
def find_best_split(X, y, max_depth=None, current_depth=0):
    """Find the best split for a node based on Gini impurity."""
    if len(np.unique(y)) == 1 or (max_depth is not None and current_depth >= max_depth):
        return (None, np.bincount(y).argmax())
    best_gain = -1
    best_feature = None
    best_threshold = None
    parent_gini = gini_impurity(y)
    for feature in range(X.shape[1]):
        unique_values = np.unique(X[:, feature])
        for i in range(len(unique_values) - 1):
            threshold = (unique_values[i] + unique_values[i + 1]) / 2
            left_indices = X[:, feature] <= threshold
            right_indices = X[:, feature] > threshold
            if not np.any(left_indices) or not np.any(right_indices):
                continue
            left_gini = gini_impurity(y[left_indices])
            right_gini = gini_impurity(y[right_indices])
            weighted_gini = (len(y[left_indices]) * left_gini + len(y[right_indices]) * right_gini) / len(y)
            gain = parent_gini - weighted_gini
            if gain > best_gain:
                best_gain = gain
                best_feature = feature
                best_threshold = threshold
    if best_gain == -1:
        return (None, np.bincount(y).argmax())
    left_indices = X[:, best_feature] <= best_threshold
    right_indices = X[:, best_feature] > best_threshold
    left_node = find_best_split(X[left_indices], y[left_indices], max_depth, current_depth + 1)
    right_node = find_best_split(X[right_indices], y[right_indices], max_depth, current_depth + 1)
    return (best_feature, best_threshold, left_node, right_node)
