import numpy as np
def metric(name: str):
    """Factory producing a validated distance function."""

    def validate(vec1, vec2):
        if not (isinstance(vec1, (list, tuple, np.ndarray)) and isinstance(vec2, (list, tuple, np.ndarray))):
            return False
        if len(vec1) != len(vec2) or len(vec1) == 0:
            return False
        try:
            vec1 = np.array(vec1, dtype=float)
            vec2 = np.array(vec2, dtype=float)
        except ValueError:
            return False
        return (vec1, vec2)

    def euclidean(vec1, vec2):
        (vec1, vec2) = validate(vec1, vec2)
        if vec1 is False:
            return -1
        return round(np.sqrt(np.sum((vec1 - vec2) ** 2)), 4)

    def manhattan(vec1, vec2):
        (vec1, vec2) = validate(vec1, vec2)
        if vec1 is False:
            return -1
        return round(np.sum(np.abs(vec1 - vec2)), 4)

    def chebyshev(vec1, vec2):
        (vec1, vec2) = validate(vec1, vec2)
        if vec1 is False:
            return -1
        return round(np.max(np.abs(vec1 - vec2)), 4)

    def cosine(vec1, vec2):
        (vec1, vec2) = validate(vec1, vec2)
        if vec1 is False or np.linalg.norm(vec1) == 0 or np.linalg.norm(vec2) == 0:
            return -1
        return round(1 - np.dot(vec1, vec2) / (np.linalg.norm(vec1) * np.linalg.norm(vec2)), 4)
    metrics = {'euclidean': euclidean, 'manhattan': manhattan, 'chebyshev': chebyshev, 'cosine': cosine}
    if name not in metrics:
        return lambda *args: -1
    return metrics[name]

def calculate_score(hand: list[int]) -> int:
    """Calculate the score of a hand in Blackjack."""
    score = sum(hand)
    aces = hand.count(1)
    while score <= 11 and aces > 0:
        score += 10
        aces -= 1
    return min(score, 21)
