from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool) -> Tuple[bool, bool]:
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_continuous(space: Space) -> bool:
        if isinstance(space, Box):
            return True
        elif isinstance(space, (Tuple, Dict)):
            if isinstance(space, Tuple):
                return all((check_continuous(sub_space) for sub_space in space.spaces))
            else:
                return all((check_continuous(sub_space) for sub_space in space.spaces.values()))
        return False
    cont_action = not tuple_action or check_continuous(env.action_space)
    cont_obs = not tuple_obs or check_continuous(env.observation_space)
    return (cont_action, cont_obs)

import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters
    ----------
    X_train : np.ndarray
        2-D array with shape (n_samples, n_features) containing the training
        features.
    y_train : np.ndarray
        1-D array of length n_samples containing the training targets.
    X_test : np.ndarray
        2-D array with shape (m_samples, n_features) containing the test
        features to predict.
    n_estimators : int, default=10
        Number of boosting iterations.
    learning_rate : float, default=0.1
        Shrinkage factor applied to each tree’s prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """

    def mse(y_true, y_pred):
        return np.mean((y_true - y_pred) ** 2)

    def split_node(X, y, index, value):
        left_mask = X[:, index] < value
        right_mask = X[:, index] >= value
        return (X[left_mask], X[right_mask], y[left_mask], y[right_mask])

    def get_split(X, y):
        (best_index, best_value, best_score, best_groups) = (999, 999, 999, None)
        for index in range(X.shape[1]):
            for value in np.unique(X[:, index]):
                (X_left, X_right, y_left, y_right) = split_node(X, y, index, value)
                if len(y_left) == 0 or len(y_right) == 0:
                    continue
                (mse_left, mse_right) = (mse(y_left, np.mean(y_left)), mse(y_right, np.mean(y_right)))
                score = mse_left + mse_right
                if score < best_score:
                    (best_index, best_value, best_score, best_groups) = (index, value, score, ((X_left, y_left), (X_right, y_right)))
        return {'index': best_index, 'value': best_value, 'groups': best_groups}

    def build_tree(X, y, depth):
        if depth == max_depth or len(y) == 0:
            return np.mean(y)
        split = get_split(X, y)
        (left, right) = split['groups']
        split['left'] = build_tree(left[0], left[1], depth + 1)
        split['right'] = build_tree(right[0], right[1], depth + 1)
        return split

    def predict_tree(node, row):
        if row[node['index']] < node['value']:
            if isinstance(node['left'], dict):
                return predict_tree(node['left'], row)
            else:
                return node['left']
        elif isinstance(node['right'], dict):
            return predict_tree(node['right'], row)
        else:
            return node['right']

    def predict(X, tree):
        return [predict_tree(tree, row) for row in X]
    F = np.full_like(y_train, np.mean(y_train))
    predictions = np.zeros(X_test.shape[0])
    for _ in range(n_estimators):
        residuals = y_train - F
        tree = build_tree(X_train, residuals, 0)
        F += learning_rate * np.array(predict(X_train, tree))
        predictions += learning_rate * np.array(predict(X_test, tree))
    return [round(float(pred), 4) for pred in predictions]
