def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """
    if not X_train:
        return []

    def sse(y):
        mean_y = np.mean(y)
        return np.sum((y - mean_y) ** 2)

    def split_data(X, y, split_val):
        left_idx = X <= split_val
        right_idx = X > split_val
        return (X[left_idx], X[right_idx], y[left_idx], y[right_idx])

    def build_tree(X, y, depth=0):
        if depth == max_depth or len(X) < 2:
            return np.mean(y)
        best_sse = float('inf')
        best_split = None
        for split_val in X:
            (X_left, X_right, y_left, y_right) = split_data(X, y, split_val)
            if len(X_left) == 0 or len(X_right) == 0:
                continue
            current_sse = sse(y_left) + sse(y_right)
            if current_sse < best_sse:
                best_sse = current_sse
                best_split = (X_left, X_right, y_left, y_right, split_val)
        if best_split is None:
            return np.mean(y)
        (X_left, X_right, y_left, y_right, split_val) = best_split
        left_tree = build_tree(X_left, y_left, depth + 1)
        right_tree = build_tree(X_right, y_right, depth + 1)
        return (split_val, left_tree, right_tree)

    def predict_tree(tree, x):
        if isinstance(tree, float):
            return tree
        (split_val, left_tree, right_tree) = tree
        if x <= split_val:
            return predict_tree(left_tree, x)
        else:
            return predict_tree(right_tree, x)
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    X_test = np.array(X_test)
    tree = build_tree(X_train, y_train)
    predictions = [predict_tree(tree, x) for x in X_test]
    return predictions
