def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
    sorted_items = sorted(frequent_items, key=lambda x: (-frequent_items[x], x))
    root = FPNode(None, None)
    for transaction in transactions:
        transaction = sorted([item for item in transaction if item in frequent_items], key=lambda x: sorted_items.index(x))
        if transaction:
            insert_transaction(root, transaction, frequent_items)
    header_table = defaultdict(list)
    for node in root.iter_nodes():
        if node.item is not None:
            header_table[node.item].append(node)

    def mine_tree(header_table, prefix, frequent_itemsets):
        for item in sorted(header_table, key=lambda x: frequent_items[x]):
            new_prefix = prefix + (item,)
            frequent_itemsets.append(new_prefix)
            suffix_patterns = construct_conditional_tree(header_table[item])
            if suffix_patterns:
                conditional_tree = build_tree(suffix_patterns)
                mine_tree(conditional_tree.header_table, new_prefix, frequent_itemsets)
