    mine_tree(header_table, (), frequent_itemsets)
    return sorted(frequent_itemsets, key=lambda x: (len(x), x))
class FPNode:

    def __init__(self, item, count, parent):
        self.item = item
        self.count = count
        self.parent = parent
        self.children = {}
        self.link = None

    def add(self, item, count):
        if item in self.children:
            self.children[item].count += count
        else:
            child = FPNode(item, count, self)
            self.children[item] = child
            if self.link is not None:
                child.link = self.link

    def iter_nodes(self):
        yield self
        for child in self.children.values():
            yield from child.iter_nodes()
def construct_conditional_tree(nodes):
    conditional_patterns = []
    for node in nodes:
        pattern = []
        while node.parent.item is not None:
            pattern.append(node.parent.item)
            node = node.parent
        if pattern:
            conditional_patterns.append((tuple(pattern), node.count))
    return conditional_patterns
