import math
def c(n):
    if n == 1:
        return 0
    else:
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n
def build_tree(data, height_limit, current_depth=0):
    if current_depth >= height_limit or len(data) <= 1:
        return {'data': data, 'depth': current_depth}
    feature = np.random.randint(data.shape[1])
    (min_val, max_val) = (np.min(data[:, feature]), np.max(data[:, feature]))
    if min_val == max_val:
        return {'data': data, 'depth': current_depth}
    threshold = np.random.uniform(min_val, max_val)
    left_data = data[data[:, feature] < threshold]
    right_data = data[data[:, feature] >= threshold]
    return {'feature': feature, 'threshold': threshold, 'left': build_tree(left_data, height_limit, current_depth + 1), 'right': build_tree(right_data, height_limit, current_depth + 1), 'depth': current_depth}
def path_length(tree, x, current_depth=0):
    if 'data' in tree:
        n = len(tree['data'])
        return current_depth + c(n)
    (feature, threshold) = (tree['feature'], tree['threshold'])
    if x[feature] < threshold:
        return path_length(tree['left'], x, current_depth + 1)
    else:
        return path_length(tree['right'], x, current_depth + 1)
def average_path_length(data, n_trees, sample_size, height_limit):
    path_lengths = np.zeros(len(data))
    for _ in range(n_trees):
        if sample_size >= len(data):
            sample = data
        else:
            sample = data[np.random.choice(len(data), sample_size, replace=False)]
        tree = build_tree(sample, height_limit)
        for (i, x) in enumerate(data):
            path_lengths[i] += path_length(tree, x)
    path_lengths /= n_trees
    return path_lengths
def anomaly_score(path_lengths, n_samples):
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples
    return 2 ** (-path_lengths / phi)
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    (n_samples, n_features) = data.shape
    height_limit = math.ceil(math.log2(sample_size))
    path_lengths = average_path_length(data, n_trees, sample_size, height_limit)
    scores = anomaly_score(path_lengths, n_samples)
    threshold = np.percentile(scores, (1 - epsilon) * 100)
    outliers = np.where(scores > threshold)[0]
    return sorted(outliers.tolist())
